import { useState } from "react";

export default function Board() {
  return (
    <>
      <div className="board-row">
        <Square number="1" />
        <Square number="2" />
        <Square number="3" />
      </div>
      <div className="board-row">
        <Square number="4" />
        <Square number="5" />
        <Square number="6" />
      </div>
      <div className="board-row">
        <Square number="7" />
        <Square number="8" />
        <Square number="9" />
      </div>
    </>
  );

  function Square({ number }) {
    useState [value, setValue] = useState(number)

    function handleClick() {
      console.log("Clicked! " + number);
      setValue
    }
    return (
      <button className="square" onClick={handleClick}>
        {number}
      </button>
    );
  }
}
